import React, { Component } from 'react';
import Web3 from 'web3'
import './App.css';

import Election from '../abis/Election.json'

import MyNavbar from './MyNavbar.js'
//import Main from './Main.js'

class App extends Component {

  async componentDidMount() {
    await this.loadWeb3()
    await this.loadBlockchainData()
  }

  async loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
      window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
    }
  }

  async loadBlockchainData() {
    const web3 = window.web3
    const accounts = await web3.eth.getAccounts()
    this.setState({ account: accounts[0] })

    const networkId = await web3.eth.net.getId()
    const networkData = Election.networks[networkId]
    if(networkData) {
        const election = new web3.eth.Contract(Election.abi, networkData.address)
        this.setState({ election })

        const candidatesCount = await this.state.election.methods.candidatesCount().call()
        this.setState({candidatesCount})
              console.log("No. of candidates:", candidatesCount)

              const can1 = await this.state.election.methods.candidates(1).call()
              console.log("Trump's data:", can1[0], can1[1], can1[2])
              const can2 = await this.state.election.methods.candidates(2).call()
              console.log("Biden's data:", can2[0], can2[1], can2[2])
              const can3 = await this.state.election.methods.candidates(3).call()
              console.log("RM's data:", can3[0], can3[1], can3[2])

              const isVoted = await this.state.election.methods.voters(accounts[0]).call()
              console.log("account 1's data:", accounts[0], isVoted)

        this.setState({ loading: false })
    } else {
        window.alert('Election contract not deployed to detected network.')
    }
  }

  constructor(props) {
    super(props)
    this.state = {
      account: '',
      loading: true, 
      candidatesCount: 0,
      value: 0,
      candidates: []
    }
    //add the following line in "<Main />":
    //<Main vote={this.vote} />
    this.vote = this.vote.bind(this)
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  vote(id) {
    this.setState({loading: true})
    this.state.election.methods.vote(id).send({from: this.state.account})
    .once('receipt', (receipt) => {
      this.setState({loading: false})
    })
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
    alert('You chose Candidate ' + this.state.value);
    this.vote(this.state.value)
    event.preventDefault();
  }

  render() {
    return (
      <div className="App">

      <MyNavbar account={this.state.account}/>

        <div id="main">
            { this.state.loading 
            ? <div id="loader" className="text-center"><p className="text-center">Loading...</p></div>
            : <div>
               <h1>Candidates Count: {this.state.candidatesCount}</h1>
               <hr/>

               <h1>Result</h1>
               <table id="table">
                    <tbody>
                      <tr>
                        <th>#</th>
                        <th>Candidate Name</th>
                        <th>Votes</th>
                      </tr>
                      <tr>
                        <td>1</td>
                        <td>Donald</td>
                        <td>123</td>
                      </tr>
                        <tr>
                        <td>2</td>
                        <td>Joe</td>
                        <td>456</td>
                      </tr>
                        <tr>
                        <td>3</td>
                        <td>RM</td>
                        <td>789</td>
                      </tr>
                    </tbody>
                </table>

                <hr/>
                <h1>Please vote:</h1>

                <form id="form" onSubmit={this.handleSubmit}>

                <select value={this.state.value} onChange={this.handleChange} >
                  <option value="0" disabled >(Choose a president)</option>
                  <option value="1" >Donald Trump</option>
                  <option value="2" >Joe Biden</option>
                  <option value="3" >Kim Namjoon</option>
                </select>
                <br/><br/>
                <input type="submit" value="Submit" />

                </form>

            </div>
            }
        </div>


      </div>

    );
  }
}

export default App;
